import React, {Component} from 'react';
import ClientDataService from '../logic/ClientDataService';

export default class Update extends Component{
  constructor(props){
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.getClient = this.getClient.bind(this);
    this.updateClient = this.updateClient.bind(this);
    this.state = {
        currentClient:
            {
                id : null,
                firstName : "",
                lastName : "",
                email : "",
                licencePlate : "",
                phoneNumber : ""
            },
        message : ""
    };
  }

  componentDidMount()
  {
      this.getClient(this.props.match.params.id);
      this.setState(prevState => ({
          currentClient: {
              ...prevState.currentClient,
              id: this.props.match.params.id
          }
      }));
  }

  getClient = (id) =>{
    ClientDataService.getClient(id).then(response => {
      this.setState({
        currentClient: response.data
      });
      console.log(response.data);
    })
    .catch(e => {
      console.log(e)
    });
  }

    handleChange = (e) => {
        const val = e.target.value;
        const tid = e.target.id;
        this.setState(prevState => ({
            currentClient: {
                ...prevState.currentClient,
                [tid]: val
            }
        }));
        console.log(val + " " + tid)
    }

    updateClient = () =>{
        ClientDataService.update(
            this.state.currentClient.id,
            this.state.currentClient
        )
            .then(response => {
                console.log(response.data);
                this.setState({
                    message: "The client was updated successfully!"
                });
            })
            .catch(e => {
                console.log(e);
            });
    }

    render() {
        let { currentClient } = this.state;
        return(
            <div>
                <span className="h3">
                <h1>Update Client</h1>
                <hr/>
                <br/>
                <center>
                    <div className="col-12 col-lg-4 mt-2 hv-center">
                        <form>
                            <div className="form-group text-left">
                                <input onChange = {this.handleChange} type="name" className="form-control" id="firstName" placeholder="Name"/>
                            </div>
                            <div className="form-group text-left">
                                <input onChange = {this.handleChange} type="name" className="form-control" id="lastName" placeholder="Name"/>
                            </div>
                            <div className="form-group text-left">
                                <input onChange = {this.handleChange}
                                   type="licensePlate"
                                   className="form-control"
                                   id="licensePlate"
                                   placeholder="License Plate"
                                />
                            </div>
                            <div className="form-group text-left">
                                <input onChange = {this.handleChange}
                                   type="phoneNr"
                                   className="form-control"
                                   id="phoneNumber"
                                   placeholder="Phone Number"
                                />
                            </div>
                            <div className="form-group text-left">
                                <input onChange = {this.handleChange}
                                   type="email"
                                   className="form-control"
                                   id="email"
                                   placeholder="Email"
                            />
                            </div>
                            <a onClick={this.handleSubmit} className="btn btn-info" role="button">
                                Update Client
                            </a>
                            <br/>
                        </form>
                    </div>
                </center>{/*TODO center tag to div align center*/}
                </span>
                <p>{this.state.message}</p>
            </div>
        )
    }
}
